beforeSaveCustomScript:
$typeNames = array\push($typeNames, 'Індив', 'Дует', 'Трио');
$clientCount = type;

name = string\concatenate(hall.number,  '/ ', array\at($typeNames, $clientCount - 1), ' - ', assignedUser.lastName);

if (!isCustomPrice) {
    //price calculation
    $categoryId = record\attribute("User", assignedUserId, 'trainerCategoryId');
    $categoryIndivId = record\findOne(
        'CategoryIndivPrice', 'createdAt', 'desc', "trainerCategoryId=", $categoryId, "clientCount=", $clientCount
    );
    $indivPrice = record\attribute("CategoryIndivPrice", $categoryIndivId, 'price');
    $indivPercent = record\attribute("CategoryIndivPrice", $categoryIndivId, 'percent');

    price = $indivPrice;
    percent = 100 - $indivPercent;
}
