beforeSaveCustomScript:
name = string\concatenate(hall.number,  '/ ', group.name);
startDateOnly =  array\at(string\split(dateStart, ' '), 0);

//forwarding
branchName = hall.branchName;

if (replacementId == null) {
    assignedUserId = group.userId;
    assignedUserName = group.userName;
    replacedWho = null;
    color = group.color;
} else {
    assignedUserId = replacementId;
    assignedUserName = replacementName;
    //forwading
    replacedWho = group.userId;
    color = '#a60306';
}
